pragma solidity ^0.4.18;

contract HandshakeProtocol {

        struct Handshake {
                uint value;
                address currency; // address(0) for eth
                uint deadline;
                S state;
        }

        /*
        struct Token {
                address adr;
                address price;
        }

        mapping(uint => Token) internal token;
        */

        struct Escrow {
                uint balance;
                uint deadline;
                mapping(address => uint) from;
                address[] payers;
        }

        struct Payment {
                T term;
                uint last; 
                uint deadline;
        }

        struct Governance {
                uint quorum;
                uint deadline;
                mapping(address => bool) approved;
                address[] approvers;
        }

        enum T { None, COD, Net30, Vesting }
        enum S { Inited, Shaked, Accepted, Rejected, Done, Cancelled }
        enum P { None, Pending, Shaked, Unshaked }

        Handshake[] internal handshakes;
        mapping(uint => address) internal payee;
        mapping(uint => mapping(address => P)) internal payer;
        mapping(uint => Escrow) internal escrow;
        mapping(uint => Payment) internal payment;
        mapping(uint => mapping(bytes32 => Governance)) internal governance;

        event __init(uint hid, uint offchain);

        event __debug__shake(uint hid, uint escrow);
        event __debug__unshake(uint hid, uint escrow);
        event __debug__withdraw(uint hid, uint amount, uint escrow);

        uint internal defaultQuorum = 50; // 50%
        uint internal reviewWindow = 7 days;
        uint internal resolveWindow = 7 days;
        uint internal voteWindow = 1 days;

        modifier onlyPayee(uint hid) { 
                require(msg.sender == payee[hid]); 
                _;  
        }


        function HandshakeProtocol() public { 
                handshakes.length = 1;  // avoid index 0 problem
        }


        function init(
                uint value,  // value of the contract. 0 if there is no money involved. 
                T term, // None, COD, Net30, Vesting
                uint escrowDate, // if value > 0
                uint deliveryDate,  // 0 if there is no deadline
                uint offchain
        ) 
                public 
        {
                _init(value, term, escrowDate, deliveryDate, address(0), msg.sender, offchain);
        }


        function initByPayee(
                uint value,  
                T term, 
                uint escrowDate, 
                uint deliveryDate,
                address _payer,
                uint offchain
        ) 
                public 
        {
                _init(value, term, escrowDate, deliveryDate, _payer, msg.sender, offchain);
        }


        event __debug__init(uint hid, T term);

        function _init(
                uint value, 
                T term,
                uint escrowDate, 
                uint deliveryDate, 
                address _payer,
                address _payee,
                uint offchain
        ) 
                internal 
                returns (uint hid)
        {
                hid = handshakes.length++;

                Handshake storage h = handshakes[hid];
                h.state = S.Inited;
                h.deadline = deliveryDate > 0? now + deliveryDate * 1 days : 0;
                payer[hid][_payer] = P.Pending;

                if (value > 0) {
                        h.value = value * 1 wei;
                        escrow[hid].deadline = now + escrowDate * 1 days;
                        payee[hid] = _payee;
                        payment[hid].term = term;
                        payment[hid].deadline = deliveryDate + 
                                (term == T.Net30? 
                                        30 days: 
                                        (term == T.Vesting?
                                                0:
                                                7 days)); // COD
                }

                __init(hid, offchain);
                __debug__init(hid, payment[hid].term);
        }


        function initByPayer(
                uint value, 
                T term,
                uint deliveryDate, 
                address _payee, 
                uint offchain
        ) 
                public 
                payable
        {
                shake(_init(value, term, now + 1 days, deliveryDate, msg.sender, _payee, offchain), offchain);
        }


        event __shake(uint hid, S state, uint balance, uint offchain);

        function shake(uint hid, uint offchain) payable public {
                Handshake storage h = handshakes[hid];
                require(h.state == S.Inited);

                Escrow storage e = escrow[hid];
                require(now <= e.deadline);
                if (e.from[msg.sender] == 0) e.payers.push(msg.sender);
                e.from[msg.sender] += msg.value;
                e.balance += msg.value;
                payer[hid][msg.sender] = P.Shaked;

                if (e.payers.length == 2) {
                        governance[hid]["reject"].quorum = defaultQuorum;
                        governance[hid]["cancel"].quorum = defaultQuorum;
                }

                if (e.balance >= h.value) {
                        h.state = S.Shaked;
                        if (payment[hid].term == T.Vesting)
                                payment[hid].last = now;
                }

                __shake(hid, h.state, e.balance, offchain); 
                __debug__shake(hid, e.balance);
        }


        event __unshake(uint hid, S state, uint balance, uint offchain);

        function unshake(uint hid, uint offchain) public {
                Handshake storage h = handshakes[hid];
                require(h.state == S.Inited);

                Escrow storage e = escrow[hid];
                uint amount = e.from[msg.sender];
                require(amount > 0);
                e.balance -= amount;
                e.from[msg.sender] = 0;
                payer[hid][msg.sender] = P.Unshaked;

                // remove msg.sender from payer list
                for (uint i = 0; i < e.payers.length; i++) 
                        if (e.payers[i] == msg.sender) {
                                for (uint j = i; j < e.payers.length-1; j++) 
                                        e.payers[j] = e.payers[j+1];
                                delete e.payers[e.payers.length-1];
                                e.payers.length--;
                                break;
                        }

                msg.sender.transfer(amount);

                __unshake(hid, h.state, e.balance, offchain); 
                __debug__unshake(hid, e.balance);
        }


        event __deliver(uint hid, S state, uint offchain);

        function deliver(uint hid, uint offchain) public onlyPayee(hid) {
                Handshake storage h = handshakes[hid];
                require(h.state == S.Shaked && now <= h.deadline);
                /*
                // if it's a digital token 
                if (token[hid].adr != address(0)) {
                        IToken t = IToken(token[hid].adr);
                        for (uint i = 0; i < escrow.payers.lenghth; i++) {
                                t.transferFrom(payee[hid], escrow.payers[i], escrow.from[payers[i]]/token[hid].price);
                        }
                */
                h.state = S.Accepted;
                __deliver(hid, h.state, offchain);
        }


        event __withdraw(uint hid, S state, uint offchain);
        event __debug__withdraw(uint hid, uint amount, uint escrow, S state, T term);

        function withdraw(uint hid, uint offchain) public onlyPayee(hid) {
                require(h.state != S.Done);

                Escrow storage e = escrow[hid];
                require(e.balance > 0);

                Handshake storage h = handshakes[hid];
                Payment storage p = payment[hid];
                uint amount;

                if (h.state == S.Accepted && now >= p.deadline)
                        amount = e.balance;
                else if (h.state == S.Shaked && p.term == T.Vesting) {
                        if (now < p.deadline)
                                amount = e.balance * (now - p.last) / (p.deadline - p.last);
                        else 
                                amount = e.balance;
                        p.last = now;
                }

                e.balance -= amount;
                if (e.balance <= 0)
                        h.state = S.Done;

                msg.sender.transfer(amount);

                __withdraw(hid, h.state, offchain);
                __debug__withdraw(hid, amount, e.balance, h.state, p.term);
        }

        event __reject(uint hid, S state, uint offchain);

        function reject(uint hid, uint offchain) public { // DAO?
                Handshake storage h = handshakes[hid];
                h.state = S.Rejected;
                payment[hid].deadline = now + resolveWindow;
                __reject(hid, h.state, offchain);
        }


        event __accept(uint hid, S state, uint offchain);

        function accept(uint hid, uint offchain) public { // DAO?
                Handshake storage h = handshakes[hid];
                h.state = S.Accepted;
                payment[hid].deadline = now;
                __accept(hid, h.state, offchain);
        }


        event __cancel(uint hid, S state, uint offchain);

        function cancel(uint hid, uint offchain) public {

                if (_approved(hid, keccak256("cancel", hid, offchain))) {

                        Handshake storage h = handshakes[hid];
                        Payment storage p = payment[hid];
                        Escrow storage e = escrow[hid];

                        require((h.state == S.Shaked && p.term == T.Vesting) ||
                                (h.state == S.Rejected && now >= p.deadline));

                        h.state = S.Cancelled;

                        // refund
                        // TODO: unit test
                        uint total;
                        for (uint i = 0; i < e.payers.length; i++) 
                                total += e.from[e.payers[i]];

                        for (uint j = 0; j < e.payers.length; j++) 
                                if (e.from[e.payers[j]] > 0) {
                                        uint amt = e.from[e.payers[j]] * e.balance / total;
                                        e.from[e.payers[j]] = 0; // wipe out
                                        e.payers[j].transfer(amt);
                                }
                }

                __cancel(hid, handshakes[hid].state, offchain);
        }


        function _approved(uint hid, bytes32 fname) internal returns (bool) {
                Escrow storage e = escrow[hid];
                require(e.from[msg.sender] > 0);
                if (e.payers.length == 1) {
                        // one-person group. automatic consensus.
                        return true;
                } else {
                        // mutli-people group. need group consensus.
                        Governance storage g = governance[hid][fname];
                        if (now >= g.deadline) {
                                // voting data reset
                                for (uint i = 0; i < g.approvers.length; i++) 
                                        g.approved[g.approvers[i]] = false;
                                delete g.approvers;
                        } else
                                require(!g.approved[msg.sender]);

                        g.approved[msg.sender] = true;
                        g.approvers.push(msg.sender);
                        if (g.approvers.length == 1) g.deadline = now + voteWindow; 

                        if (now < g.deadline && g.approvers.length * 100 >= e.payers.length * g.quorum) {
                                g.deadline = now - 1; // trigger voting data reset
                                return true;

                        }
                }
        }

        function handshakesOf(address uadr) public view returns (uint[] result){
                uint n;
                for (uint i = handshakes.length - 1; i >= 1; i--) 
                        if (payee[i] == uadr || 
                            (payer[i][uadr] == P.Pending || payer[i][uadr] == P.Shaked)) 
                                n++;

                result = new uint[](n);

                uint k;
                for (uint j = handshakes.length - 1; j >= 1; j--) 
                        if (payee[j] == uadr || 
                            (payer[j][uadr] == P.Pending || payer[j][uadr] == P.Shaked))
                                result[k++] = j;
        }
}
